<div class="row">
	<form>
		<div class="input-field col s6 m4">
			<select>
				<option value="aluga">Aluga</option>
				<option value="vende">Vende</option>
			</select>
			<label>Status</label>
		</div>
		<div class="input-field col s6 m4">
			<select>
				<option value="1">Alvenaria</option>
				<option value="2">Apartamento</option>
				<option value="3">Duplex</option>
			</select>
			<label>Tipo de Imóvel</label>
		</div>
		<div class="input-field col s6 m4">
			<select>
				<option value="1">São Roque</option>
				<option value="2">Mairinque</option>
				<option value="3">Sorocaba</option>
			</select>
			<label>Cidade</label>
		</div>
		<div class="input-field col s6 m3">
			<select>
				<option value="1">1</option>
				<option value="2">2</option>
				<option value="3">3</option>
				<option value="4">Mais</option>
			</select>
			<label>Dormitórios</label>
		</div>
		<div class="input-field col s12 m4">
			<select>
				<option value="1">Até R$ 500,00</option>
				<option value="2">R$ 500,00 até R$1.000,00</option>
			    <option value="3">R$ $1.000,00 até R$5.000,00</option>
			    <option value="4">Acima de R$5.000,00</option>
			</select>
			<label>Valor</label>
		</div>
		<div class="input-field col s12 m3">
			<input class="validate" type="text" name="bairro">
			<label>Bairro</label>
		</div>
		<div class="input-field col s12 m2">
			<button class="btn deep-orange darken-1 right">Filtrar</button>
		</div>
	</form>
</div>